package Lec_3;

public class pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int row=1;
		
		while(row<=5) {
			// work
			int i=1;
			while(i<=5) {
				System.out.print("*");
				i=i+1;
			}
			System.out.println();
			row=row+1;
			
		}
	}

}
